mapper.select:

where 1 = 1 
and id = #{id} 
and req_token = #{reqToken} 
 and req_ip = #{reqIp} 
and req_api = #{reqApi} 
and res_code = #{resCode} 
and user_id = #{userId} 
and admin_id = #{adminId} 
and start_time >= #{sTime} 
and start_time <= #{eTime} 
order by 

	id desc 
	start_time desc, id desc 
	cost_time desc, id desc 
 	id desc

select sum(cost_time) as cost_time_count from sp_apilog 
where 1 = 1 
and id = #{id} 
and req_token = #{reqToken} 
 and req_ip = #{reqIp} 
and req_api = #{reqApi} 
and res_code = #{resCode} 
and user_id = #{userId} 
and admin_id = #{adminId} 
and start_time >= #{sTime} 
and start_time <= #{eTime}
